  // Preload critical images
  const criticalImages = [
    'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEjKhaBasharat-profile-hero.jpg'
  ];

  criticalImages.forEach(src => {
    const img = new Image();
    img.src = src;
  });
});

