(function() {
  var items = <data:post.commentJso/>;
  var msgs = <data:post.commentMsgs/>;
  var config = <data:post.commentConfig/>;
  var cursor = null;
  if (items && items.length > 0) { cursor = parseInt(items[items.length - 1].timestamp) + 1; }
  var render = function() {
    if (window.goog && window.goog.comments) {
      var holder = document.getElementById('comment-holder');
      window.goog.comments.render(holder, { 'id': config.postId, 'data': items, 'config': config, 'messages': msgs });
    }
  };
  if (window.goog && window.goog.comments) { render(); }
  else { window.goog = window.goog || {}; window.goog.comments = window.goog.comments || {}; window.goog.comments.loadQueue = window.goog.comments.loadQueue || []; window.goog.comments.loadQueue.push(render); }
})();
