// 1. IMPROVED TYPEWRITER
const typeText = document.querySelector(".typewrite-text");
const words = ["Electronics Engineer.", "AI Specialist.", "SEO Expert.", "Python Developer."];
let wordIndex = 0;
let charIndex = 0;
let isDeleting = false;

function typeEffect() {
  const currentWord = words[wordIndex];

  if (isDeleting) {
    charIndex--;
    typeText.textContent = currentWord.substring(0, charIndex);
  } else {
    charIndex++;
    typeText.textContent = currentWord.substring(0, charIndex);
  }

  let typeSpeed = isDeleting ? 40 : 100;

  // Logic: If finished typing word
  if (!isDeleting && charIndex === currentWord.length) {
    isDeleting = true;
    typeSpeed = 2000; // Pause at end of word
  } 
  // Logic: If finished deleting
  else if (isDeleting && charIndex === 0) {
    isDeleting = false;
    wordIndex = (wordIndex + 1) % words.length;
    typeSpeed = 500; // Pause before new word
  }

  setTimeout(typeEffect, typeSpeed);
}

// 2. MOBILE MENU TOGGLE
const hamburger = document.querySelector(".hamburger");
const navMenu = document.querySelector(".nav-links");
const navLinks = document.querySelectorAll(".nav-link");

hamburger.addEventListener("click", () => {
  hamburger.classList.toggle("active");
  navMenu.classList.toggle("active");
});

navLinks.forEach(n => n.addEventListener("click", () => {
  hamburger.classList.remove("active");
  navMenu.classList.remove("active");
}));

// 3. PARTICLE SYSTEM
const canvas = document.getElementById('canvas-bg');
const ctx = canvas.getContext('2d');
let particlesArray;

function initCanvas() {
  canvas.width = window.innerWidth;
  canvas.height = window.innerHeight;
  particlesArray = [];
  let numberOfParticles = (canvas.height * canvas.width) / 14000;
  for (let i = 0; i < numberOfParticles; i++) {
    let size = (Math.random() * 2) + 1;
    let x = Math.random() * canvas.width;
    let y = Math.random() * canvas.height;
    let directionX = (Math.random() * 0.2) - 0.1;
    let directionY = (Math.random() * 0.2) - 0.1;
    let color = '#6366f1';
    particlesArray.push({x, y, directionX, directionY, size, color});
  }
}

function animate() {
  requestAnimationFrame(animate);
  ctx.clearRect(0, 0, innerWidth, innerHeight);
  for (let i = 0; i < particlesArray.length; i++) {
    let p = particlesArray[i];
    if (p.x > canvas.width || p.x < 0) p.directionX = -p.directionX;
    if (p.y > canvas.height || p.y < 0) p.directionY = -p.directionY;
    p.x += p.directionX;
    p.y += p.directionY;

    ctx.beginPath();
    ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2, false);
    ctx.fillStyle = 'rgba(99, 102, 241, 0.4)';
    ctx.fill();

    for (let j = i; j < particlesArray.length; j++) {
       let p2 = particlesArray[j];
       let distance = ((p.x - p2.x) * (p.x - p2.x)) + ((p.y - p2.y) * (p.y - p2.y));
       if (distance < (canvas.width/7) * (canvas.height/7)) {
         ctx.strokeStyle = 'rgba(99, 102, 241, 0.05)';
         ctx.lineWidth = 1;
         ctx.beginPath();
         ctx.moveTo(p.x, p.y);
         ctx.lineTo(p2.x, p2.y);
         ctx.stroke();
       }
    }
  }
}

window.addEventListener('resize', initCanvas);
document.addEventListener('DOMContentLoaded', () => {
   initCanvas();
   animate();
   typeEffect();
});
